package com.huaxia.system.bean;

import java.io.Serializable;

import com.huaxia.base.bean.NativeID;

/**
 * @description
 * @author : zhangy
 * @date : 2016年12月22日 上午11:27:04
 */
public class UserRole extends NativeID implements Serializable{
	
	/**
	 * 
	 */
	private static final long serialVersionUID = 1L;

	private Long roleId;
	
	private Long userId;
	
	

	public UserRole() {
		super();
		// TODO Auto-generated constructor stub
	}
	
	

	public UserRole(Long roleId, Long userId) {
		super();
		this.roleId = roleId;
		this.userId = userId;
	}



	public Long getRoleId() {
		return roleId;
	}

	public void setRoleId(Long roleId) {
		this.roleId = roleId;
	}

	public Long getUserId() {
		return userId;
	}

	public void setUserId(Long userId) {
		this.userId = userId;
	}
	
	
}
